import React from "react";

const MealItemForm = () => {
  return (
    <form>
      <input
        label='Amount'
        input={{
          id: "amount",
          type: "number",
          min: "1",
          max: "5",
          step: "1",
          defaultValue: "1",
        }}
      />
      <button className='m-2 bg-danger'>
        <h3>+ Add TO CART</h3>
      </button>
    </form>
  );
};

export default MealItemForm;
