import React from "react";

const CartItem = (props) => {
  const price = `$${props.price.toFixed(2)}`;
  return (
    <li>
      <div>
        <h2>{props.name}</h2>
        <div>
          <span>{price}</span>
          <span> x {price.amount}</span>
        </div>
        <button>-</button>
        <button>+</button>
      </div>
    </li>
  );
};

export default CartItem;
